import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';


@Component({
  selector: 'app-amount-input-form',
  templateUrl: './amount-input-form.component.html',
  styleUrls: ['./amount-input-form.component.css']
})
export class AmountInputFormComponent {

  inputForm!: FormGroup;

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit() {
    this.inputForm = this.formBuilder.group({
      firstName: new FormControl('', [Validators.required, Validators.pattern('^\d+([\.,][0-9]{1,2})?$')])
    });
  }

  onSubmit() {
    console.log(this.inputForm.get('firstName')?.value);

    if (this.inputForm.invalid) {
      this.inputForm.markAllAsTouched();
    }
  }

  get firstName() {
    return this.inputForm.get('firstName')!;
  }
  get lastName() {
    return this.inputForm.get('lastName')!;
  }
  get occupation() {
    return this.inputForm.get('occupation')!;
  }
}
