import { Component } from '@angular/core';
import { FormBuilder, FormControl, FormGroup, Validators } from '@angular/forms';
import { Subject } from 'rxjs';


@Component({
  selector: 'app-amount-input-form',
  templateUrl: './amount-input-form.component.html',
  styleUrls: ['./amount-input-form.component.css']
})
export class AmountInputFormComponent {

  inputForm!: FormGroup;


  //this is a Subject (multicastable Observable) others can subscribe to receive its value whenever updated.
  validAmount: Subject<number> = new Subject<number>;

  constructor(private formBuilder: FormBuilder) {}

  ngOnInit(): void {
    this.inputForm = this.formBuilder.group({
      amount: new FormControl('', [Validators.required, 
                                   Validators.pattern("^\\d+([\\.,][0-9]{1,2})?$")])
    });  //js the backslash has to be escaped
  }

  onSubmit() {
    //console.log(this.inputForm.get('amount')?.value);
    if (this.inputForm.valid) {
      console.log("valid");
      this.validAmount.next(this.amount.value);
    }


  }

  get amount() {
    return this.inputForm.get('amount')!;
  }

}
